	(54, 1, '2008-09-17 16:53:25', '2008-09-17 21:53:25', '<div id="WRchTxt2" class="wysiwyg_viewer_skins_WRichTextNewSkintxtNew">\r\n<p class="font_8"><span class="color_14"><strong>Professionalinfo</strong></span><strong><span class="color_14"></span></strong></p>\r\n\r\n\r\n<hr />\r\n<p class="font_8"></p>\r\n\r\n</div>\r\n<div id="WRchTxta" class="wysiwyg_viewer_skins_WRichTextNewSkintxtNew">\r\n<p class="font_8">I\'m a Paragraph. Click here to add your own text and edit me. Its easy. Just click Edit Text or double click me and you can start adding your own content and make changes to the font. Feel free to drag and drop me anywhere you like on your page. Im a great place for you to write more. Tell a story about yourself.</p>\r\n<p class="font_8"></p>\r\n\r\n<div id="WRchTxtb" class="wysiwyg_viewer_skins_WRichTextNewSkintxtNew">\r\n<p class="font_8"><span class="color_14"><strong>Work experience</strong></span></p>\r\n\r\n\r\n<hr />\r\n<p class="font_8">Job position. click to edit</p>\r\n\r\n</div>\r\n<div id="WRchTxt1q" class="wysiwyg_viewer_skins_WRichTextNewSkintxtNew">\r\n<p class="font_7"><span class="color_18"></span>2010 - present</p>\r\n<p class="font_7">I\'m a Paragraph. Click here to add your own text and edit me. Its easy. Just click Edit Text or double click me and you can start adding your own content and make changes to the font. Feel free to drag and drop me anywhere you like on your page. Im a great place for you to write more. Tell a story about yourself.</p>\r\n<p class="font_7"></p>\r\n<p class="font_8">Job position. click to edit</p>\r\n<p class="font_7">2010 - present</p>\r\n<p class="font_7">I\'m a Paragraph. Click here to add your own text and edit me. Its easy. Just click Edit Text or double click me and you can start adding your own content and make changes to the font. Feel free to drag and drop me anywhere you like on your page. Im a great place for you to write more. Tell a story about yourself.</p>\r\n<p class="font_7"></p>\r\n<p class="font_8">Job position. click to edit</p>\r\n<p class="font_7">2010 - present</p>\r\n<p class="font_7"></p>\r\n<p class="font_8">I\'m a Paragraph. Click here to add your own text and edit me. Its easy. Just click Edit Text or double click me and you can start adding your own content and make changes to the font. Feel free to drag and drop me anywhere you like on your page. Im a great place for you to write more. Tell a story about yourself.</p>\r\n\r\n</div>\r\n</div>', 'CV', '', 'publish', 'open', 'open', '', 'cv', '', '', '2014-10-24 13:50:41', '2014-10-24 12:50:41', '', 0, 'http://dev.wpcoder.com/dan/wordpress/?page_id=2', 0, 'page', '', 0),
